sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

    var getBookTitle = new GlideAjax('BookTitleUtils');
    getBookTitle.addParam('sysparm_name', 'findBookTitle');
    getBookTitle.addParam('sysparm_book_title', newValue);
	//use getXml for one piece of information (JSON = object + retrieve multiple values)
    getBookTitle.getXML(responseParse);

    function responseParse(response) {
        var answerfromXML = response.responseXML.documentElement.getAttribute('answer');
		//'answer' already exists in the XML that is being sent back to you
		g_form.setValue('requested_book_reserved_books', answerfromXML);
    }

}
