sys_script_include.script:
var BookTitleUtils = Class.create();
BookTitleUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

findBookTitle: function() {

	var returnValue;

	var book = this.getParameter('sysparm_book_title');

        var findBook = new GlideRecord('x_1342778_library_book');
        findBook.addQuery('sys_id', book);
        findBook.query();

        if (findBook.next()) {
			returnValue = findBook.title;
		}

        return returnValue;
    },


    type: 'BookTitleUtils'
});
